// Utils
function moneyToFloat(val) {
    if (val.indexOf("R$") > -1) {
        val = val.replace("R$", "");
        val = val.trim();
    }

    val = val.replace(".", "");
    val = val.replace(",", ".");
    val = parseFloat(val);
    if (isNaN(val)) {
        return 0;
    }
    return val;
}
function getDateTimeNow() {
    var date = new Date();
    var dia = date.getDate();
    if (dia < 10) {
        dia = "0" + dia;
    }
    var mes = date.getMonth() + 1;
    if (mes < 10) {
        mes = "0" + mes;
    }

    var ano = date.getFullYear();

    var hora = date.getHours();
    if (hora < 10) {
        hora = "0" + hora;
    }

    var minutos = date.getMinutes();
    if (minutos < 10) {
        minutos = "0" + minutos;
    }

    var dateTime = [ano, mes, dia].join("-") + " " + hora + ":" + minutos;
    return dateTime
}
function getDateNow() {
    var date = new Date();
    var dia = date.getDate();
    if (dia < 10) {
        dia = "0" + dia;
    }
    var mes = date.getMonth() + 1;
    if (mes < 10) {
        mes = "0" + mes;
    }

    var ano = date.getFullYear();


    var dateTime = [ano, mes, dia].join("-");
    return dateTime
}
function executeInsert(query, constraints) {
    var dataSource = "/jdbc/CastilhoCustom";
    var ic = new javax.naming.InitialContext();
    var ds = ic.lookup(dataSource);

    log.info("wfkTransferenciaDeCustoEntreObras.beforeTaskSave: executandoQuery");
    log.info(query);
    try {
        var conn = ds.getConnection();
        var stmt = conn.prepareStatement(query, Packages.java.sql.Statement.RETURN_GENERATED_KEYS);

        var counter = 1;
        for (var i = 0; i < constraints.length; i++) {
            var val = constraints[i];
            if (val.type == "int") {
                stmt.setInt(counter, val.value);
            }
            else if (val.type == "float") {
                stmt.setFloat(counter, val.value);
            }
            else if (val.type == "date") {
                stmt.setString(counter, val.value);
            }
            else if (val.type == "datetime") {
                stmt.setString(counter, val.value);
            } else {
                stmt.setString(counter, val.value);
            }
            counter++;
        }

        log.info("wfkTransferenciaDeCustoEntreObras.beforeTaskSave: executandoQuery"+query.length)

       var hasResultSet = stmt.execute();
        if (hasResultSet) {
            var rs = stmt.getResultSet();
            if (rs.next()) {
                var id = rs.getInt(1);
                log.info("id");
                log.dir(id);
                return id;
            }
        }



    } catch (e) {
        log.error("ERRO==============> " + e.message);
        throw e;
    } finally {
        if (stmt != null) {
            stmt.close();
        }
        if (conn != null) {
            conn.close();
        }
    }
}
function executeUpdate(query, constraints) {
    var dataSource = "/jdbc/CastilhoCustom";
    var ic = new javax.naming.InitialContext();
    var ds = ic.lookup(dataSource);

    log.info("wfkTransferenciaDeCustoEntreObras.beforeTaskSave: executandoQuery");
    log.info(query);
    try {
        var conn = ds.getConnection();
        var stmt = conn.prepareStatement(query, Packages.java.sql.Statement.RETURN_GENERATED_KEYS);

        var counter = 1;
        for (var i = 0; i < constraints.length; i++) {
            var val = constraints[i];
            if (val.type == "int") {
                stmt.setInt(counter, val.value);
            }
            else if (val.type == "float") {
                stmt.setFloat(counter, val.value);
            }
            else if (val.type == "date") {
                stmt.setString(counter, val.value);
            }
            else if (val.type == "datetime") {
                stmt.setString(counter, val.value);
            } else {
                stmt.setString(counter, val.value);
            }
            counter++;
        }

        log.info("wfkTransferenciaDeCustoEntreObras.beforeTaskSave: executandoQuery"+constraints.length)

       stmt.execute();


    } catch (e) {
        log.error("ERRO==============> " + e.message);
        throw e;
    } finally {
        if (stmt != null) {
            stmt.close();
        }
        if (conn != null) {
            conn.close();
        }
    }
}
function FormataValorParaMoeda(valor) {
    return 'R$ ' + valor.toFixed(2).replace('.', ',');
}

// Define Aprovador Obra Origem
function servicetask10(attempt, message) {
    var decisao = hAPI.getCardValue("decisao");
    if (decisao=="") {
        // Se Decisao == "", significar que o Processo foi iniciado e Define o Primeiro Aprovador
        defineProximoAprovador();
        hAPI.setCardValue("decisaoAprovadorObraOrigem", "Aprovação");
        return true;
    }

    if (decisao == "Aprovado") {
        marcaAprovacaoDoAprovador();
        verificaSeAprovadorTambemAprovaPelaDestino();

        var aprovadoTotalmente = verificaSeTotalmenteAprovado();
        if (aprovadoTotalmente) {
            hAPI.setCardValue("decisaoAprovadorObraOrigem", "Aprovado");
            return true;
        }
        else{
            defineProximoAprovador();
            hAPI.setCardValue("decisaoAprovadorObraOrigem", "Aprovação");
            return true;
        }
    }

    if (decisao == "Reprovado" || decisao == "Reprovado Automaticamente") {
        hAPI.setCardValue("decisaoAprovadorObraOrigem", "Reprovado");
        return true;
    }
}

function defineProximoAprovador() {
    var aprovadoEngenheiroObraOrigem = hAPI.getCardValue("aprovadoEngenheiroObraOrigem");
    var aprovadoCoordenadorObraOrigem = hAPI.getCardValue("aprovadoCoordenadorObraOrigem");
    var aprovadoDiretorObraOrigem = hAPI.getCardValue("aprovadoDiretorObraOrigem");

    if (aprovadoEngenheiroObraOrigem != "true" && hAPI.getCardValue("engenheiroObraOrigem") != "") {
        var engenheiroObraOrigem = hAPI.getCardValue("engenheiroObraOrigem");
        hAPI.setCardValue("usuarioAprovadorOrigem", engenheiroObraOrigem);
    }
    else if (aprovadoCoordenadorObraOrigem != "true") {
        var coordenadorObraOrigem = hAPI.getCardValue("coordenadorObraOrigem");
        hAPI.setCardValue("usuarioAprovadorOrigem", coordenadorObraOrigem);
    }
    else if (aprovadoDiretorObraOrigem != "true") {
        var diretorObraOrigem = hAPI.getCardValue("diretorObraOrigem");
        hAPI.setCardValue("usuarioAprovadorOrigem", diretorObraOrigem);
    } 
    else {
        hAPI.setCardValue("aprovadoObraOrigem", "true");
        hAPI.setCardValue("usuarioAprovadorOrigem", "");
    }

    return hAPI.getCardValue("usuarioAprovadorOrigem");
}
function marcaAprovacaoDoAprovador() {
    var aprovador = hAPI.getCardValue("usuarioAprovadorOrigem");

    var engenheiroObraOrigem = hAPI.getCardValue("engenheiroObraOrigem");
    var coordenadorObraOrigem = hAPI.getCardValue("coordenadorObraOrigem");
    var diretorObraOrigem = hAPI.getCardValue("diretorObraOrigem");

    if (aprovador == engenheiroObraOrigem) {
        hAPI.setCardValue("aprovadoEngenheiroObraOrigem", "true");
    }
    if (aprovador == coordenadorObraOrigem) {
        hAPI.setCardValue("aprovadoCoordenadorObraOrigem", "true");
    }
    if (aprovador == diretorObraOrigem) {
        hAPI.setCardValue("aprovadoDiretorObraOrigem", "true");
    }
}
function verificaSeAprovadorTambemAprovaPelaDestino() {
    var aprovador = hAPI.getCardValue("usuarioAprovadorOrigem");

    var engenheiroObraDestino = hAPI.getCardValue("engenheiroObraDestino");
    var coordenadorObrDestino = hAPI.getCardValue("coordenadorObraDestino");
    var diretorObraOriDestino = hAPI.getCardValue("diretorObraDestino");

    if (aprovador == engenheiroObraDestino) {
        hAPI.setCardValue("aprovadoEngenheiroObraDestino", "true");
    }
    else if (aprovador == coordenadorObrDestino) {
        hAPI.setCardValue("aprovadoCoordenadorObraDestino", "true");
    }
    else if (aprovador == diretorObraOriDestino) {
        hAPI.setCardValue("aprovadoDiretorObraDestino", "true");
    }
}
function verificaSeTotalmenteAprovado(){
    var aprovadoEngenheiroObraOrigem = hAPI.getCardValue("aprovadoEngenheiroObraOrigem");
    var aprovadoCoordenadorObraOrigem = hAPI.getCardValue("aprovadoCoordenadorObraOrigem");
    var aprovadoDiretorObraOrigem = hAPI.getCardValue("aprovadoDiretorObraOrigem");

    if ((aprovadoEngenheiroObraOrigem == "true" || hAPI.getCardValue("engenheiroObraOrigem") == "") && aprovadoCoordenadorObraOrigem == "true" && aprovadoDiretorObraOrigem == "true") {        
        return true;
    }

    return false;
}

// Define Aprovador Obra Destino
function servicetask9(attempt, message) {
    var decisao = hAPI.getCardValue("decisao");
    if (decisao=="") {
        // Se Decisao == "", significar que o Processo foi iniciado

        //Verifica se os Aprovadores não são todos iguais entre Origem e Destino
        var isTodosAprovadoresIguaisOrigemDestino = verificaSeTotalmenteAprovado();
        if (isTodosAprovadoresIguaisOrigemDestino == true) {
            hAPI.setCardValue("decisaoAprovadorObraDestino", "Aprovado");
            return true;
        }

        // Define primeiro Aprovador
        defineProximoAprovador();
        hAPI.setCardValue("decisaoAprovadorObraDestino", "Aprovação");
        if (hAPI.getCardValue("usuarioAprovadorDestino") == "") {
            hAPI.setCardValue("usuarioAprovadorDestino", "gabriel.persike");
        }
        return true;
    }

    if (decisao == "Aprovado") {
        marcaAprovacaoDoAprovador();
        var aprovadoTotalmente = verificaSeTotalmenteAprovado();
        if (aprovadoTotalmente) {
            hAPI.setCardValue("decisaoAprovadorObraDestino", "Aprovado");
            return true;
        }
        else{
            defineProximoAprovador();
            hAPI.setCardValue("decisaoAprovadorObraDestino", "Aprovação");
            return true;
        }
    }

    if (decisao == "Reprovado" || decisao == "Reprovado Automaticamente") {
        hAPI.setCardValue("decisaoAprovadorObraDestino", "Reprovado");
        return true;
    }
}

function defineProximoAprovador(){
    // Verifica quais níveis já aprovaram
    var isAprovadoPeloEngenheiro = hAPI.getCardValue("aprovadoEngenheiroObraDestino") == "true";
    var isAprovadoCoordenador = hAPI.getCardValue("aprovadoCoordenadorObraDestino") == "true";
    var isAprovadoDiretor = hAPI.getCardValue("aprovadoDiretorObraDestino") == "true";

    // Verifica casos que o nível de Aprovação é o mesmo na Origem e Destino
    var mesmoEngenheiroOrigemDestino = hAPI.getCardValue("engenheiroObraDestino") == hAPI.getCardValue("engenheiroObraOrigem");
    var mesmoCoordenadorOrigemDestino = hAPI.getCardValue("coordenadorObraDestino") == hAPI.getCardValue("coordenadorObraOrigem");
    var mesmoDiretorOrigemDestino = hAPI.getCardValue("diretorObraDestino") == hAPI.getCardValue("diretorObraOrigem");


    // Caso o Aprovador também Aprova na Obra Origem, não defini ele como Aprovador na Destino para não ocorrer aprovações Duplicadas
    if (!isAprovadoPeloEngenheiro && !mesmoEngenheiroOrigemDestino && hAPI.getCardValue("engenheiroObraDestino") != "") {
        hAPI.setCardValue("usuarioAprovadorDestino", hAPI.getCardValue("engenheiroObraDestino"));
        return;
    }
    if (!isAprovadoCoordenador && !mesmoCoordenadorOrigemDestino) {
        hAPI.setCardValue("usuarioAprovadorDestino", hAPI.getCardValue("coordenadorObraDestino"));
        return;
    }
    if (!isAprovadoDiretor && !mesmoDiretorOrigemDestino) {
        hAPI.setCardValue("usuarioAprovadorDestino", hAPI.getCardValue("diretorObraDestino"));
        return;
    }
}
function marcaAprovacaoDoAprovador() {
    var aprovador = hAPI.getCardValue("usuarioAprovadorDestino");

    var engenheiroObraDestino = hAPI.getCardValue("engenheiroObraDestino");
    var coordenadorObraDestino = hAPI.getCardValue("coordenadorObraDestino");
    var diretorObraDestino = hAPI.getCardValue("diretorObraDestino");

    if (aprovador == engenheiroObraDestino) {
        hAPI.setCardValue("aprovadoEngenheiroObraDestino","true");
    }
    if (aprovador == coordenadorObraDestino) {
        hAPI.setCardValue("aprovadoCoordenadorObraDestino","true");
    }
    if (aprovador == diretorObraDestino) {
        hAPI.setCardValue("aprovadoDiretorObraDestino","true");
    }
}
function verificaSeTotalmenteAprovado(){
    // Verifica casos que o nível de Aprovação é o mesmo na Origem e Destino
    var mesmoEngenheiroOrigemDestino = hAPI.getCardValue("engenheiroObraDestino") == hAPI.getCardValue("engenheiroObraOrigem");
    var mesmoCoordenadorOrigemDestino = hAPI.getCardValue("coordenadorObraDestino") == hAPI.getCardValue("coordenadorObraOrigem");
    var mesmoDiretorOrigemDestino = hAPI.getCardValue("diretorObraDestino") == hAPI.getCardValue("diretorObraOrigem");

    var isAprovadoEngenheiro = hAPI.getCardValue("aprovadoEngenheiroObraDestino") == "true";
    var isAprovadoCoordenador = hAPI.getCardValue("aprovadoCoordenadorObraDestino") == "true";
    var isAprovadoDiretor = hAPI.getCardValue("aprovadoDiretorObraDestino") == "true";

    if ((isAprovadoEngenheiro || mesmoEngenheiroOrigemDestino || hAPI.getCardValue("engenheiroObraDestino") == "") && (isAprovadoCoordenador || mesmoCoordenadorOrigemDestino) && (isAprovadoDiretor || mesmoDiretorOrigemDestino)) {
        return true;
    }

    return false;
}
